mapper.delete:
DELETE board_carpool, board_reply_carpool
       FROM board_carpool LEFT JOIN board_reply_carpool ON board_carpool.board_cp_no =
       board_reply_carpool.board_cp_no WHERE board_carpool.board_cp_no=#{boardCpNo};

mapper.update:
update board_carpool set board_cp_title =
       #{boardCpTitle} , board_cp_content = #{boardCpContent}, board_cp_date = #{boardCpDate},
       board_cp_place = #{boardCpPlace} where board_cp_no = #{boardCpNo}
