mapper.insert:
    insert into member(
                        member_no,
                        member_domain,
                        member_id,
                        member_password,
                        member_name,
                        member_age,
                        member_birth,
                        member_email,
                        member_gender,
                        member_mobile,
                        member_nickname,
                        member_profile_image,
                        member_register_date)
                values(
                seq_member_no.nextval

				,#{MEM_DOMAIN}


				,#{MEM_ID}


				,#{MEM_PASSWORD}


				,#{MEM_NAME}


				,#{MEM_AGE}


				,#{MEM_BIRTHDAY}


				,#{MEM_EMAIL}


				,#{MEM_GENDER}


				,#{MEM_MOBILE}


				,#{MEM_NICKNAME}


				,#{MEM_PROFILE_IMAGE}


				,#{MEM_REGISTER_DATE}

				)
